mapper.insert:
INSERT INTO TBL_REPORT(
                        REP_TITLE
                        ,REP_CONTENT
                        ,REP_DATE
                        ,REP_ID
                        ,REP_PRC
                    )
VALUES (
             #{ repTitle }
            ,#{ repContent }
            ,#{ repDate }
            ,#{ repId }
            ,#{ repPrc }
        )

mapper.update:
UPDATE  TBL_REPORT
   SET  REP_TITLE = #{ repTitle }
        ,REP_CONTENT = #{ repContent }
        ,REP_DATE = #{ repDate }
        ,REP_ID = #{ repId }
        ,REP_PRC = #{ repPrc }
 WHERE  REP_NO = #{ repNo }

mapper.delete:
DELETE
  FROM TBL_REPORT
 WHERE REP_NO = #{ repNo }
